from math import sqrt
import numpy as np
from dtaidistance import dtw
import pandas as pd

# %%
df = pd.read_csv('../editData/walk_in_data.csv')
# clean data
df['Sta3n'] = df['Sta3n'].apply(str)
df['date'] = pd.to_datetime(df['date'])
# set index
df = df.set_index(['date'], drop=True)

#%%
sta6aList = df['sta6a'].unique().tolist()
# sta6aList.remove('*Missing*')
matrix = np.zeros((len(sta6aList), len(sta6aList)))
for i in range(len(sta6aList)):
    for j in range(i + 1, len(sta6aList)):
        # get two lists
        hospital1 = df[df.sta6a == sta6aList[i]][['n_walkins']].sort_index().resample('D').asfreq().fillna(0).to_numpy().reshape(-1)
        hospital2 = df[df.sta6a == sta6aList[j]][['n_walkins']].sort_index().resample('D').asfreq().fillna(0).to_numpy().reshape(-1)
        # calculate distance and store in matrix
        matrix[i, j] = dtw.distance(hospital1, hospital2)

np.savetxt('distances.csv', matrix, delimiter=',')

with open('distances.csv', 'a') as f:
    f.write('\n')
    f.write(','.join(sta6aList))


